DROP TABLE IF EXISTS `comment`;
CREATE TABLE `comment`  (
  `idComment` int(11) UNSIGNED NOT NULL AUTO_INCREMENT,
  `idUser` varchar(255) CHARACTER SET utf8 COLLATE utf8_vietnamese_ci NOT NULL,
  `idPost` varchar(255) CHARACTER SET utf8 COLLATE utf8_vietnamese_ci NOT NULL,
  `dateComment` datetime(0) NULL DEFAULT NULL,
  `content` varchar(255) CHARACTER SET utf8 COLLATE utf8_vietnamese_ci NULL DEFAULT NULL,
  PRIMARY KEY (`idComment`) USING BTREE,
  INDEX `fk_comment_account`(`idUser`) USING BTREE,
  INDEX `fk_comment_post`(`idPost`) USING BTREE
) ENGINE = MyISAM AUTO_INCREMENT = 12 CHARACTER SET = utf8 COLLATE = utf8_vietnamese_ci ROW_FORMAT = Dynamic;

