DROP TABLE IF EXISTS `post`;
CREATE TABLE `post`  (
  `postId` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,
  `title` varchar(255) CHARACTER SET utf8 COLLATE utf8_vietnamese_ci NULL DEFAULT NULL,
  `subTitle` varchar(255) CHARACTER SET utf8 COLLATE utf8_vietnamese_ci NULL DEFAULT NULL,
  `datePost` date NULL DEFAULT NULL,
  `viewCount` int(11) NULL DEFAULT NULL,
  `postStatus` varchar(255) CHARACTER SET utf8 COLLATE utf8_vietnamese_ci NULL DEFAULT NULL,
  `content` text CHARACTER SET utf8 COLLATE utf8_vietnamese_ci NULL,
  `idCat` int(10) NULL DEFAULT NULL,
  `type` varchar(255) CHARACTER SET utf8 COLLATE utf8_vietnamese_ci NULL DEFAULT NULL,
  `dateUpload` date NULL DEFAULT NULL,
  `srcImage` varchar(255) CHARACTER SET utf8 COLLATE utf8_vietnamese_ci NULL DEFAULT NULL,
  `idWriter` int(10) NULL DEFAULT NULL,
  `idEditor` int(10) NULL DEFAULT NULL,
  `dateProcess` date NULL DEFAULT NULL,
  `reasonReject` varchar(255) CHARACTER SET utf8 COLLATE utf8_vietnamese_ci NULL DEFAULT NULL,
  PRIMARY KEY (`postId`) USING BTREE,
  INDEX `fk_post_category`(`idCat`) USING BTREE,
  INDEX `fk_post_writer`(`idWriter`) USING BTREE,
  INDEX `fk_post_editor`(`idEditor`) USING BTREE
) ENGINE = MyISAM AUTO_INCREMENT = 2021 CHARACTER SET = utf8 COLLATE = utf8_vietnamese_ci ROW_FORMAT = Dynamic;

